class CompanyListingDetailPage extends StatefulWidget {
  final Company _company;

  CompanyListingDetailPage(this._company, {Key? key}) : super(key: key);

  @override
  State<CompanyListingDetailPage> createState() =>
      _CompanyListingDetailPageState();
}

class _CompanyListingDetailPageState extends State<CompanyListingDetailPage>
    with TickerProviderStateMixin {
  late final TabController _tabController;

  @override
  void initState() {
    super.initState();
    _tabController = TabController(length: 5, vsync: this);
  }

  @override
  void dispose() {
    _tabController.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: SafeArea(
        child: NestedScrollView(
          headerSliverBuilder: (BuildContext context, bool innerBoxIsScrolled) {
            return <Widget>[
              SliverAppBar(),
              SliverToBoxAdapter(
                key: UniqueKey(),
                child: Padding(
                  padding: const EdgeInsets.all(16),
                  child: Row(
                    children: [
                      Image.asset(
                        widget._company.logo,
                        width: 80,
                        height: 80,
                      ),
                      Expanded(
                        child: Padding(
                          padding: const EdgeInsets.all(12.0),
                          child: Column(
                            mainAxisAlignment: MainAxisAlignment.start,
                            crossAxisAlignment: CrossAxisAlignment.start,
                            children: [
                              Text(
                                widget._company.companyName,
                                overflow: TextOverflow.ellipsis,
                                maxLines: 1,
                                style: const TextStyle(
                                    fontSize: 24, fontWeight: FontWeight.bold),
                              ),
                              Wrap(
                                spacing: 8,
                                children: [
                                  Text(
                                    widget._company.industryType,
                                    overflow: TextOverflow.ellipsis,
                                    maxLines: 1,
                                    style: TextStyle(
                                        fontSize: 20, color: Colors.grey[500]),
                                  ),
                                  /*Text(
                                  '\$ ${widget._company.revenue}',
                                  overflow: TextOverflow.ellipsis,
                                  maxLines: 1,
                                  style: TextStyle(
                                      fontSize: 20, color: Colors.grey[500]),
                                )*/
                                ],
                              ),
                            ],
                          ),
                        ),
                      )
                    ],
                  ),
                ),
              ),
              SliverOverlapAbsorber(
                handle: NestedScrollView.sliverOverlapAbsorberHandleFor(context),
                sliver: SliverSafeArea(
                  sliver:  SliverPersistentHeader(
                      pinned: true,
                      delegate: _TabSliverPersistentHeaderDelegate(_tabController)),
                ),
              )
            ];
          },
          body: TabBarView(
            controller: _tabController,
            children: [
              RevenueAndValuationPage(),
              CompetitorPage(),
              AboutCompanyPage(),
              EmployeeDataPage(),
              NewsPage()
            ],
          ),
        ),
      ),
    );
  }
}

class _TabSliverPersistentHeaderDelegate
    extends SliverPersistentHeaderDelegate {
  final TabController _tabController;

  _TabSliverPersistentHeaderDelegate(this._tabController);

  @override
  Widget build(
      BuildContext context, double shrinkOffset, bool overlapsContent) {
    return TabBar(
      tabAlignment: TabAlignment.start,
      isScrollable: true,
      controller: _tabController,
      tabs: const <Widget>[
        Tab(text: 'Revenue & Valuation'),
        Tab(text: 'Competitors'),
        Tab(text: 'About'),
        Tab(text: 'Employee Data'),
        Tab(text: 'News')
      ],
    );
  }

  @override
  double get maxExtent => 46;

  @override
  double get minExtent => 46;

  @override
  bool shouldRebuild(covariant SliverPersistentHeaderDelegate oldDelegate) =>
      true;
}

